mapper.insert:

	SELECT NVL(MAX(notice_code),1)+1 FROM ar_notice


INSERT INTO ar_notice (
					notice_code
					, notice_title
					, notice_content

					, up_enddate

					, notice_writer
					, notice_type
					) 
				values(
					#{noticeCode}
					, #{noticeTitle}
					, #{noticeContent}

					, #{upEnddate}

					, 'Admin'
					, #{noticeType}
					)

mapper.select:
SELECT rt.declare_code, declare_code_name(rt.declare_reason) as declare_reason, rt.declare_content, rt.member_id, rt.declare_member, rt.declare_regdate FROM ( SELECT rownum r, de.declare_code, de.declare_reason, de.declare_content, de.member_id, de.declare_member, de.declare_regdate FROM (SELECT * FROM ar_declare where declare_status = 'N'
AND declare_content LIKE '%' || #{declareContent} || '%'
AND declare_reason = #{declareReason}
order by declare_code desc) de
where rownum <= #{pageNum} * #{amount} )rt
WHERE rt.r > ( #{pageNum} - 1) * #{amount}

select count(*) from ar_declare where declare_code > 0 AND declare_status = 'N'
AND declare_content LIKE '%' || #{declareContent} || '%'
AND declare_reason = #{declareReason}

SELECT rt.declare_code, find_suspend_status_name(rt.suspend_status) as suspend_status , declare_code_name(rt.declare_reason) as declare_reason , rt.declare_content , rt.member_id , rt.declare_member , rt.declare_regdate FROM ( SELECT rownum r, de.suspend_status, de.declare_code, de.declare_reason, de.declare_content, de.member_id, de.declare_member, de.declare_regdate FROM (SELECT * FROM ar_declare where declare_status = 'Y'
AND declare_content LIKE '%' || #{declareContent} || '%'
AND declare_reason = #{declareReason}
order by declare_code desc) de
where rownum <= #{pageNum} * #{amount} )rt
WHERE rt.r > ( #{pageNum} - 1) * #{amount}

select count(*) from ar_declare where declare_code > 0 AND declare_status = 'Y'
AND declare_content LIKE '%' || #{declareContent} || '%'
AND declare_reason = #{declareReason}
