mapper.insert:

  	SELECT NVL(MAX(que_code),0)+1 FROM ar_inquiry

  	insert into ar_inquiry 
  	(que_code  
  		,product_code 
  		,que_content
  		,member_id
  		,que_secret
  		,que_regdate
  		,que_ans
  		,seller_id)
values (
		#{queCode}
		,#{productCode}
		,#{queContent}
		,#{memberId}
		,#{queSecret}
		,sysdate
		,#{queAns}
		,#{sellerId}
		)
