mapper.select:
  		select count(*)
		from ar_member
		where member_id = #{memberId}

mapper.insert:
  		insert into ar_member (
  				member_id
  				, member_pw
  				, member_name
  				, member_birth
  				, member_addr
  				, member_details_addr
  				, member_email
  				, member_author
  				, member_tel
		      	 , business_number
		      	)
		values (#{memberId}
				, #{memberPw}
				, #{memberName}
				, #{memberBirth}
				, #{memberAddr}
				, #{memberDetailsAddr}
				, #{memberEmail}
				, #{memberAuthor}
				, #{memberTel}
		      	 , #{businessNumber}
		      	)

mapper.select:
  		select *
		from ar_member
		where member_author = #{memberAuthor}
		AND member_id = #{memberId}

  	  	select count(*)
		from ar_member
		where member_author = #{memberAuthor}
		AND member_id = #{memberId}
