mapper.update:
update ar_order_detail
SET order_check= 'N'
WHERE order_detail_code = #{orderDetailCode}

update ar_cancel
set cancel_status = 'N'
WHERE cancel_code = #{cancelCode}

mapper.select:
select p.product_code,p.product_name, i.que_content,m.member_id,i.que_regdate,i.que_ans,i.que_code,i.member_id from ar_products p JOIN ar_inquiry i ON p.product_code = i.product_code JOIN ar_member m ON m.member_id = i.member_id WHERE p.member_id= #{memberId}
AND m.member_name like '%'||#{searchValue}||'%'
AND m.member_id like '%'||#{searchValue}||'%'
AND p.product_code like '%'||#{searchValue}||'%'
AND i.que_regdate BETWEEN #{fromDate} AND #{toDate}

mapper.update:
update ar_inquiry
set que_ans = #{queAns}
WHERE
que_code = #{queCode}

mapper.select:
select * from
ar_option_detail
where product_code = #{productCode}

select p.product_code , p.product_name , NVL(o.option_last,' ') as option_last , COALESCE(o.option_detail_code,0) as option_detail_code , (p.sale_price + o.option_price) AS sale_price , COALESCE(sum(od.order_cnt),0) as order_cnt , COALESCE(SUM(return_cnt(od.order_detail_code)),0) AS return_cnt , COALESCE(SUM(cancel_cnt(od.order_detail_code)),0) AS cancel_cnt , NVL((sum(od.order_cnt)- COALESCE(SUM(return_cnt(od.order_detail_code)),0)- COALESCE(SUM(cancel_cnt(od.order_detail_code)),0))*NVL((p.sale_price + o.option_price),0) ,0) AS total_price from ar_products p LEFT OUTER JOIN ar_option_detail o ON p.product_code = o.product_code LEFT OUTER JOIN ar_order_detail od ON o.option_detail_code = od.option_detail_code FULL OUTER join ar_return r ON od.order_detail_code = r.order_detail_code where member_id = #{memberId}
AND TRUNC(od.order_date) BETWEEN TO_DATE(#{preBetw},'yy/MM/dd') AND TO_DATE(#{suBetw},'yy/MM/dd')
group by od.option_detail_code, o.option_last, o.option_price, p.product_name, o.option_detail_code, (p.sale_price + o.option_price), p.sale_price + o.option_price, o.option_price, p.product_code ORDER BY option_detail_code

