mapper.update:
UPDATE ar_option
SET
    option_name = #{optionName},
    option_value = #{optionValue}
WHERE product_code = #{productCode}
and option_code = #{optionCode}

UPDATE ar_imgs
SET

    img_name = #{imgName},


    upload_name = #{uploadName},


    img_content = #{imgContent},


    upload_path = #{uploadPath}

WHERE product_code = #{productCode}
AND img_order = #{imgOrder}

UPDATE ar_imgs
SET
	product_code = #{productCode}

        ,upload_name = #{uploadName}


        ,img_content = #{imgContent}


        ,upload_path = #{uploadPath}

WHERE product_code = #{productCode}
AND img_order = 0

mapper.select:
select m.member_id , p.product_code , p.category_code , p.product_name , p.product_content , p.product_location , p.product_cost , p.product_regdate , p.sale_price , p.product_brand , p.product_origin , p.product_makeorigin , p.delivery_cost , p.return_cost , p.exchange_cost , GET_DELI_NAME(p.delivery_service) as delivery_service , p.product_status , i.img_order , i.upload_name , i.img_content , i.upload_path , m.business_number from ar_products p ,ar_imgs i, ar_member m where p.product_code = i.product_code
and rownum < 2
and p.member_id = m.member_id and p.product_code = #{productCode}

select m.member_id , p.product_code , p.category_code , p.product_name , p.product_content , p.product_location , p.product_cost , p.product_regdate , p.sale_price , p.product_brand , p.product_origin , p.product_makeorigin , p.delivery_cost , p.return_cost , p.exchange_cost , p.delivery_service , p.product_status , i.img_order , i.upload_name , i.img_content , i.upload_path , m.business_number from ar_products p ,ar_imgs i, ar_member m where p.product_code = i.product_code AND img_order = 0
and rownum < 2
and p.member_id = m.member_id and p.product_code = #{productCode}

select * from ar_custom_inquiry
where member_id = #{memberId}

         AND custom_inquiry_regdate BETWEEN #{fromDate} AND #{toDate}
