mapper.insert:
		INSERT ALL 
		INTO ar_products (product_code
				, member_id
				, category_code
    			, product_name
                , product_content
                , product_location
    			, product_cost
    			, sale_price
    			, product_brand
    			, product_origin
    			, product_makeorigin
    			, delivery_cost
    			, return_cost
    			, exchange_cost
                , delivery_service
                , product_exstatus)
		VALUES (#{productCode}
					, #{memberId}
					, #{categoryCode}
					, #{productName}
					, #{productContent}
					, #{productLocation}
					, #{productCost}
					, #{salePrice}
					, #{productBrand}
					, #{productOrigin}
					, #{productMakeorigin}
					, #{deliveryCost}
					, #{returnCost}
					, #{exchangeCost}
					, #{deliveryService}
					, 'Y'
					)

        SELECT MAX(option_code)+1 FROM ar_option       

		INTO ar_option (option_code
                    , product_code
                    , option_order
                    , option_name
                    , option_value
                    , option_price
                    , option_sell_status
                    , option_exstatus
                    , option_count
                    , min_order 
                    , max_order
                        )
	VALUES
	    	(#{optionCode}
	    	, #{productCode}
	    	, #{optionOrder}
	    	, #{optionName}
	    	, #{optionValue}
	    	, #{optionPrice}
	    	, #{optionSellStatus}
	    	, #{optionExStatus}
	    	, #{optionCount}
	    	, #{minOrder}
	    	, #{maxOrder}
	    	)

mapper.update:
UPDATE ar_products 

SET product_exstatus = 'Y'
WHERE product_code = #{productCode}
