mapper.insert:

  		SELECT NVL(0,MAX(review_code))+1 FROM ar_review  

  	insert into ar_review 
  		(
  		review_code
  		,product_code
  		,member_id
  		,review_content
  		,review_star
  		)
	values 
		(
		#{reviewCode}
		,#{productCode}
		,#{memberId}
		,#{reviewContent}
		,#{reviewStar}
		)


		SELECT NVL(0, MAX(review_img_code))+1 FROM ar_review_img  

		insert into ar_review_img
  		(review_img_code
  		,review_code
  		,img_order
  		,img_name
  		,upload_name
  		,img_content)
  	values(
  		#{reviewImgCode}
  	   ,#{reviewCode}
  	   ,#{imgOrder}
  	   ,#{imgName}
  	   ,#{uploadName}
  	   ,#{imgContent}
